	RAISERROR('%s: Starting',0,1, @ProcName) WITH NOWAIT;

	/* SAMPLE DATA
		DECLARE @[VARIABLE_NAME] [TYPE] = ...
	--*/

	--Magic Numbers
	--DECLARE @[VARIABLE_NAME] [TYPE] = ...
	------------------------------------------------------------------------------

	------------------------------------------------------------------------------
	--Put code here
	$SELECTEDTEXT$$CURSOR$
	------------------------------------------------------------------------------

	------------------------------------------------------------------------------
	RAISERROR('%s: Done',0,1, @ProcName) WITH NOWAIT;
END
GO
